import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';


const NewPost = () => {

    let history = useHistory();


    const [author, setAuthor] = useState(null)
    const [post_pic, setPostPic] = useState(null)
    const [post_caption, setPostCaption] = useState(null)


    const addNewPost = async () => {
        let formField = new FormData()
        formField.append('author', author)
        formField.append('post_pic', post_pic)
        formField.append('post_caption', post_caption)

        await axios({
          method: 'post',
          url:'http://localhost:8000/api/posts/',
          data: formField
        }).then(response=>{
          console.log(response.data);
          history.push('/')
        })
    }

    return (
        <div className="container">
            <div className="container">
      <div className="w-75 mx-auto shadow p-5">
        <h2 className="text-center mb-4">Make A Post</h2>


        <div className="form-group">
        <label>Your Image</label>
             <input type="file" className="form-control" onChange={(e)=>setPostPic(e.target.files[0])}/>
          </div>

          <div className="form-group">
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Your Name"
              name="author"
              value={author}
              onChange={(e) => setAuthor(e.target.value)}
            />
          </div>

          <div className="form-group">
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Your Caption"
              name="post_caption"
              value={post_caption}
              onChange={(e) => setPostCaption(e.target.value)}
            />
          </div>

          <button className="btn btn-primary btn-block" onClick={addNewPost}>Submit</button>

      </div>
    </div>
        </div>
    );
};

export default NewPost;
